import React, {Component} from 'react';
import 'C:/Users/Gebruiker/Desktop/Engineering/demo/src/src/webapp/src/App.css';

class Floorplan extends Component {

    constructor(props) {
        super(props);

        this.state = {
            diffX: 0,
            diffY: 0,
            dragging: false,
            styles: {},
            scroll: 1
        }

        this._dragStart = this._dragStart.bind(this);
        this._dragging = this._dragging.bind(this);
        this._dragEnd = this._dragEnd.bind(this);
    }

    _dragStart(e) {
        let screenX;
        let screenY;

        if(e.screenX) {
            screenX = e.screenX;
            screenY = e.screenY;
        }
        else {
            console.log(e.clientX)
        }

        this.setState({
            diffX: e.screenX - e.currentTarget.getBoundingClientRect().left,
            diffY: e.screenY - e.currentTarget.getBoundingClientRect().top,
            dragging: true
        });
    }

    _dragging(e) {
        if(this.state.dragging) {
            const left = e.screenX -  this.state.diffX;
            const top = e.screenY - this.state.diffY;

            this.setState({
                styles: {
                    left: left,
                    top: top,
                    transform: this.state.styles.transform
                }
            });
        }
    }



    _dragEnd() {
        this.setState({
            dragging: false
        });
    }

    _scroll = (e) => {
        if(e.deltaY === 100) {
            this.setState({
                styles: {
                    left: e.target.getBoundingClientRect().left,
                    top: e.target.getBoundingClientRect().top,
                    transform: "scale("+ (this.state.scroll += 0.1) +")"
                }
            })
        }
        else {
            this.setState({
                styles: {
                    left: e.target.getBoundingClientRect().left,
                    top: e.target.getBoundingClientRect().top,
                    transform: "scale("+ (this.state.scroll -= 0.1) +")"
                }
            })
        }
    }

    render() {
        return (
            <img style={this.state.styles}
                 alt="" className="img"
                 id="floorplan-container-image"
                 draggable="false"
                 src={require('../Components/Images/testa.png')}
                 onMouseDown={this._dragStart}
                 onMouseMove={this._dragging}
                 onMouseUp={this._dragEnd}
                 onMouseLeave={this._dragEnd}
                 onWheel={this._scroll}
                 onTouchStart={this._dragStart}
            />
        );
    }
}

export default Floorplan;
